mapper.update:
update emp
username = #{username}, name = #{name}, gender = #{gender}, image = #{image}, dept_id = #{deptId}, entry_date = #{entryDate}, job = #{job}, salary = #{salary}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select emp.id, username, emp.name, gender, job, salary, image, entry_date, dept_id, emp.create_time, emp.update_time,dept_id
from  emp left join dept on emp.dept_id = dept.id

    emp.name like concat('%',#{name},'%')
    and gender = #{gender}
    and entry_date between #{begin} and #{end}

order by emp.update_time desc
